import styled from 'styled-components'
import { connect } from 'react-redux' 

const S = {
Wrapper: styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
  `,


Avatar: styled.div`
    border-radius: 10px 10px 10px 15px;
    width: 200px;
    height: 200px;
    display: flex;
    justify-content: center;
    align-items: center;
    img {
      width: 150px;
      height: 150px;
      border-radius: 50%;
    }
  `,
Row: styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

`,
ExtraTitle: styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    font-size: 20px;
    font-weight:600;
    border-bottom:1px solid #eee;    
`

}

const UserInfo = ({ data })=> {
  const repos = data ? data.repos : null
  const avatar = data.avatarUrl ? data.avatarUrl : '../static/images/plainDoge-700x525.jpg'
  const name = data.login ? data.login : 'No user info'
  const wow = 'WOW'
        return (
          <S.Wrapper>
            <S.Row>
            <h2>{name}</h2>
              <S.Avatar>
            <img src={avatar}/>
              </S.Avatar> { repos &&
              <>
                {repos.slice(0, 3).map(({ id, url }) => (
                      <p key={id}>
                        <a href={url} target="">
                          {url}
                        </a>
                      </p>
                    ))}
                    </>
              }
              {!repos &&
                  <S.ExtraTitle>{wow}</S.ExtraTitle>
              }
             
            </S.Row>
        </S.Wrapper>
        )
    
}

export default UserInfo
