import { Table as TableStrap } from 'reactstrap'
import styled from 'styled-components'

const S = {
  Table: styled(TableStrap)`
    margin: 0 auto;
    width:800px;
  `,

  Header: styled.thead``,

  HeaderCol: styled.th`
    border-top: 0!important;
  `,

  Body: styled.tbody``,
  Row: styled.tr``,
  Col: styled.td``,
  ProductName: styled.span`
  `,
  Text: styled.span`
  font-size:15px;
  font-weight:600;
  font-family: 'Arial';
  font-style: normal;
  `
}


const UserInfo = ({ data }) => {
  return (
    <S.Table>
      {data &&
        <S.Body>
        {data.map(user => (
          <S.Row key={user.userId}>
            <S.Col>
              <S.ProductName>
                User email:{user.email}
              </S.ProductName>
            </S.Col>
            <S.Col>
              Hero Intro:{user.hero_intro}
            </S.Col>
            <S.Col>
              Hero Magic:{user.hero_magic}
            </S.Col>
            <S.Col>
              <h3>Hero profecy:</h3><S.Text>{user.hero_profecy}</S.Text>
            </S.Col>
          </S.Row>
        ))}
        </S.Body>
      }
    
      
    

    </S.Table>
  )
}

export default UserInfo
